import React, { useEffect, useState } from 'react';
import supabase from './supabaseClient';
import Product from './Product'; // Import the Product component

function ShowProducts({ uuid }) {
  // ... (previous code)

  return (
    <div className="container">
      <h2 className="mainHeader">Products for User {uuid}</h2>
      <ul>
        {products.map((product) => (
          <li key={product.id} className="card">
            <p className="text-lg">
              <strong>Product ID:</strong> {product.id}
            </p>
            <p className="description">
              <strong>Description:</strong> {product.description}
            </p>
            <p className="text-sm">
              <strong>Price:</strong> ${product.price}
            </p>
            <button
              onClick={() => handleCommentClick(product.id)}
              className="button primary"
            >
              Add Comment
            </button>
            {selectedProductId === product.id && (
              <div>
                <form>
                  <textarea
                    value={commentText}
                    onChange={(e) => setCommentText(e.target.value)}
                    className="comment-input"
                    placeholder="Enter your comment..."
                    style={{ color: 'black' }}
                  />
                  <button onClick={handleAddComment} type="submit"> 
                    Post Comment
                  </button>
                </form>
              </div>
            )}

            {/* Render the Product component with productData */}
            {productData && <Product productData={productData} />}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ShowProducts;
