import React, { useEffect, useState } from 'react';
import supabase from './supabaseClient'; // Import your Supabase client instance here

function ShowProducts({ uuid }) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Fetch products associated with the given UUID
    async function fetchProducts() {
      const { data, error } = await supabase
        .from('products')
        .select('id, description, price')
        .eq('user_seller', uuid);

      if (error) {
        console.error('Error fetching products:', error.message);
      } else {
        setProducts(data);
      }
    }

    // Call the fetchProducts function when the component mounts
    fetchProducts();
  }, [uuid]);

  return (
    <div>
      <h2>Products for User {uuid}</h2>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <strong>Description:</strong> {product.description} - <strong>Price:</strong> {product.price}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ShowProducts;
