import React, { useEffect, useState } from 'react';
import supabase from './supabaseClient'; // Import your Supabase client instance here

// Define CSS styles for better presentation
const styles = {
  container: {
    maxWidth: '600px',
    margin: '0 auto',
    padding: '20px',
    boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.2)',
    borderRadius: '5px',
    background: '#f9f9f9',
  },
  productItem: {
    marginBottom: '10px',
    padding: '10px',
    border: '1px solid #ddd',
    borderRadius: '5px',
    backgroundColor: '#fff',
  },
};

function ShowProducts({ uuid }) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Fetch products associated with the given UUID
    async function fetchProducts() {
      const { data, error } = await supabase
        .from('products')
        .select('id, description, price')
        .eq('user_seller', uuid);

      if (error) {
        console.error('Error fetching products:', error.message);
      } else {
        setProducts(data);
      }
    }

    // Call the fetchProducts function when the component mounts
    fetchProducts();
  }, [uuid]);

  return (
    <div style={styles.container}>
      <h2>Products for User {uuid}</h2>
      <ul>
        {products.map((product) => (
          <li key={product.id} style={styles.productItem}>
            <p>
              <strong>Description:</strong> {product.description}
            </p>
            <p>
              <strong>Price:</strong> ${product.price}
            </p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ShowProducts;
